"use client";
import { useState } from 'react';
import ShopLayout from '../../pages/components/ShopLayout';

const fruitProducts = [
  { id: 1, name: 'Local Apples', price: 129, size: '1 KG', image: 'data:image/jpeg;base64,', description: 'Crisp apples harvested from nearby orchards.' },
  { id: 2, name: 'Farm Pears', price: 119, size: '1 KG', image: 'data:image/jpeg;base64,', description: 'Sweet and juicy pears, perfect for desserts.' },
  { id: 3, name: 'Banana Bunch', price: 49, size: '6 pcs', image: 'data:image/jpeg;base64,', description: 'Ripe bananas ideal for smoothies and snacks.' },
  { id: 4, name: 'Local Grapes', price: 199, size: '500 GM', image: 'data:image/jpeg;base64,', description: 'Fresh grapes with a natural sweet flavour.' },
  { id: 5, name: 'Seasonal Plums', price: 179, size: '500 GM', image: 'data:image/jpeg;base64,', description: 'Sweet-tart plums from local groves.' },
  { id: 6, name: 'Citrus Mix', price: 159, size: '1 KG', image: 'data:image/jpeg;base64,', description: 'Oranges and lemons packed for freshness.' },
];

export default function FarmFreshFruits() {
  // Independent quantity state for each product
  const [quantities, setQuantities] = useState(
    fruitProducts.reduce((acc, product) => ({ ...acc, [product.id]: 1 }), {})
  );

  const updateQty = (id, delta) => {
    setQuantities(prev => ({
      ...prev,
      [id]: Math.max(1, prev[id] + delta)
    }));
  };

  return (
    <ShopLayout>
      <div className="category-page-container">
        {/* üü¢ Standardized Centered Heading Section */}
        <div className="page-header">
          <h1 className="main-heading">Farm Fresh Fruits</h1>
          <p className="sub-heading">Seasonal fruits straight from local orchards ‚Äî flavorful and picked with care for your family.</p>
        </div>

        {/* üü¢ Standardized Product Grid */}
        <div className="product-grid">
          {fruitProducts.map((item) => {
            const currentQty = quantities[item.id];
            // ‚≠ê LIVE PRICE CALCULATION
            const totalAmount = (item.price * currentQty).toFixed(2);

            return (
              <div className="product-card" key={item.id}>
                <div className="img-holder">
                  <img src={item.image} alt={item.name} />
                </div>
                
                <h4 className="p-title">{item.name}</h4>
                <div className="p-amount">Rs. {totalAmount}</div>
                <div className="p-unit-badge">{item.size}</div>

                {/* Quantity Picker UI */}
                <div className="qty-picker">
                  <button onClick={() => updateQty(item.id, -1)} aria-label="Decrease quantity">-</button>
                  <input type="text" value={currentQty} readOnly />
                  <button onClick={() => updateQty(item.id, 1)} aria-label="Increase quantity">+</button>
                </div>
                
                {/* Green Add to Cart Button */}
                <button className="add-to-cart-btn" onClick={() => console.log('Added:', item.name)}>
                  Add to Cart
                </button>
              </div>
            );
          })}
        </div>

        <style jsx global>{`
          /* Fix: Keep body background transparent to show ShopLayout footer background */
          body { 
            margin: 0; 
            font-family: 'Inter', sans-serif; 
          }

          .category-page-container { 
            max-width: 1400px; 
            margin: 0 auto; 
            padding: 60px 5%; 
            min-height: 70vh; /* Pushes footer down */
          }

          .page-header { 
            text-align: center; 
            margin-bottom: 50px; 
          }

          .main-heading { 
            font-size: 36px; 
            font-weight: 800; 
            color: #333; 
            margin-bottom: 8px; 
          }

          .sub-heading {
            font-size: 15px;
            color: #777;
            max-width: 700px;
            margin: 0 auto;
          }

          .product-grid { 
            display: grid; 
            grid-template-columns: repeat(auto-fill, minmax(260px, 1fr)); 
            gap: 30px; 
          }

          .product-card { 
            border: 1px solid #f2f2f2; 
            border-radius: 12px; 
            padding: 24px; 
            text-align: center; 
            display: flex; 
            flex-direction: column; 
            transition: all 0.3s ease;
            background: #fff;
          }

          .product-card:hover { 
            transform: translateY(-5px); 
            box-shadow: 0 12px 30px rgba(0,0,0,0.07); 
          }

          .img-holder { 
            height: 190px; 
            display: flex; 
            align-items: center; 
            justify-content: center; 
            margin-bottom: 18px; 
          }
          .img-holder img { max-height: 100%; max-width: 100%; object-fit: contain; }

          .p-title { font-size: 16px; font-weight: 600; color: #444; height: 40px; margin: 5px 0; }
          .p-amount { font-size: 18px; font-weight: 700; color: #111; margin-bottom: 6px; }

          .p-unit-badge { 
            font-size: 11px; 
            background: #f8f8f8;
            color: #888;
            padding: 4px 12px;
            border-radius: 4px;
            display: inline-block;
            align-self: center;
            margin-bottom: 22px;
            font-weight: bold;
          }

          .qty-picker { 
            display: flex; 
            border: 1px solid #e0e0e0; 
            border-radius: 6px; 
            overflow: hidden; 
            align-self: center; 
            margin-bottom: 18px; 
          }
          .qty-picker button { background: #fff; border: none; padding: 8px 15px; cursor: pointer; font-size: 18px; }
          .qty-picker input { width: 40px; text-align: center; border-left: 1px solid #e0e0e0; border-right: 1px solid #e0e0e0; border-top: none; border-bottom: none; font-weight: 600; }

          .add-to-cart-btn { 
            background: #6aa333; 
            color: #fff; 
            border: none; 
            padding: 12px; 
            border-radius: 8px; 
            font-weight: 700; 
            font-size: 14px; 
            cursor: pointer; 
            margin-top: auto; 
            transition: background 0.2s;
          }
          .add-to-cart-btn:hover { background: #5a8d2a; }
        `}</style>
      </div>
    </ShopLayout>
  );
}