"use client";
import React, { useState } from "react";

const products = [
  { id: 1, name: "Seasonal Green Peas", price: 45.00, unit: "500 GM", image: "data:image/jpeg;base64," },
  { id: 2, name: "Fresh Sweet Corn", price: 25.00, unit: "1 PC", image: "data:image/jpeg;base64," },
  { id: 3, name: "Winter Special Red Carrot", price: 40.00, unit: "500 GM", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRSOyXT84mcnYrFAm_WS_miGidf7wPNFQCjXw&s" },
  { id: 4, name: "Fresh Cauliflower", price: 48.00, unit: "1 PC (Approx 600g)", image: "data:image/jpeg;base64," },
  { id: 5, name: "Seasonal Broad Beans", price: 55.00, unit: "250 GM", image: "data:image/jpeg;base64," },
  { id: 6, name: "Fresh Broccoli", price: 90.00, unit: "1 PC (Approx 400g)", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSSpwtbUo08tzQSgB8yKF8ZuVckVGDNU5ciew&s" },
  { id: 7, name: "Seasonal Radish (White)", price: 30.00, unit: "500 GM", image: "data:image/jpeg;base64," },
  { id: 8, name: "Organic Beetroot", price: 35.00, unit: "500 GM", image: "data:image/jpeg;base64," },
];

export default function SeasonalSpecialPage() {
  // Independent state for quantities of each product
  const [quantities, setQuantities] = useState(
    products.reduce((acc, product) => ({ ...acc, [product.id]: 1 }), {})
  );

  const updateQty = (id, delta) => {
    setQuantities(prev => ({
      ...prev,
      [id]: Math.max(1, prev[id] + delta)
    }));
  };

  return (
    <div className="category-container">
      {/* ðŸŸ¢ Centered Header Section */}
      <div className="page-header">
        <h1 className="main-title">Seasonal Specials</h1>
        <p className="sub-title">Pick the best of the seasonâ€”fresh, nutrient-dense, and farm-direct.</p>
      </div>

      {/* ðŸŸ¢ Product Grid */}
      <div className="product-grid">
        {products.map((p) => {
          const currentQty = quantities[p.id];
          const totalAmount = (p.price * currentQty).toFixed(2); // Live price calculation

          return (
            <div className="product-card" key={p.id}>
              <div className="image-wrapper">
                <img src={p.image} alt={p.name} />
              </div>
              
              <h4 className="item-name">{p.name}</h4>
              <div className="item-price">Rs. {totalAmount}</div>
              <div className="unit-badge">{p.unit}</div>

              {/* Quantity Selector */}
              <div className="qty-control">
                <button onClick={() => updateQty(p.id, -1)} aria-label="Decrease quantity">-</button>
                <input type="text" value={currentQty} readOnly />
                <button onClick={() => updateQty(p.id, 1)} aria-label="Increase quantity">+</button>
              </div>
              
              {/* Green Add to Cart Button */}
              <button className="add-to-cart-btn">Add to Cart</button>
            </div>
          );
        })}
      </div>

      <style jsx global>{`
        body { margin: 0; font-family: 'Inter', sans-serif; background: #fff; }

        .category-container { 
          max-width: 1400px; 
          margin: 0 auto; 
          padding: 60px 5%; 
        }

        .page-header { 
          text-align: center; 
          margin-bottom: 50px; 
        }

        .main-title { 
          font-size: 38px; 
          font-weight: 800; 
          color: #333; 
          margin-bottom: 8px; 
        }

        .sub-title {
          font-size: 15px;
          color: #777;
        }

        .product-grid { 
          display: grid; 
          grid-template-columns: repeat(auto-fill, minmax(240px, 1fr)); 
          gap: 30px; 
        }

        .product-card { 
          border: 1px solid #f2f2f2; 
          border-radius: 12px; 
          padding: 20px; 
          text-align: center; 
          display: flex; 
          flex-direction: column; 
          transition: all 0.3s ease;
          background: #fff;
        }

        .product-card:hover { 
          transform: translateY(-5px); 
          box-shadow: 0 12px 30px rgba(0,0,0,0.07); 
        }

        .image-wrapper { 
          height: 180px; 
          display: flex; 
          align-items: center; 
          justify-content: center; 
          margin-bottom: 15px; 
        }

        .image-wrapper img { max-height: 100%; max-width: 100%; object-fit: contain; }

        .item-name { font-size: 16px; font-weight: 600; color: #444; height: 40px; margin: 5px 0; }
        .item-price { font-size: 19px; font-weight: 700; color: #111; margin-bottom: 6px; }

        .unit-badge { 
          font-size: 11px; 
          background: #f8f8f8;
          color: #888;
          padding: 4px 12px;
          border-radius: 4px;
          display: inline-block;
          align-self: center;
          margin-bottom: 20px;
          font-weight: bold;
        }

        .qty-control { 
          display: flex; 
          border: 1px solid #e0e0e0; 
          border-radius: 6px; 
          overflow: hidden; 
          align-self: center; 
          margin-bottom: 15px; 
        }

        .qty-control button { background: #fff; border: none; padding: 8px 15px; cursor: pointer; font-size: 18px; }
        .qty-control input { width: 40px; text-align: center; border-left: 1px solid #e0e0e0; border-right: 1px solid #e0e0e0; border-top: none; border-bottom: none; font-weight: 600; }

        .add-to-cart-btn { 
          background: #6aa333; 
          color: #fff; 
          border: none; 
          padding: 12px; 
          border-radius: 8px; 
          font-weight: 700; 
          font-size: 14px; 
          cursor: pointer; 
          margin-top: auto; 
          transition: background 0.2s;
        }

        .add-to-cart-btn:hover { background: #5a8d2a; }
      `}</style>
    </div>
  );
}