"use client";
import React, { useState } from "react";
import ShopLayout from "../components/ShopLayout";

const products = [
  { id: 1, name: "Fresh Tomato", price: 32.00, unit: "500 GM", image: "data:image/jpeg;base64," },
  { id: 2, name: "Organic Potato", price: 50.00, unit: "1 KG", image: "data:image/jpeg;base64," },
  { id: 3, name: "Green Capsicum", price: 42.00, unit: "500 GM", image: "data:image/jpeg;base64," },
  { id: 4, name: "Red Onion", price: 52.00, unit: "1 KG", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSusR3U7NfSSGeiNe7SQVLQYrzm48EzFYfy3g&s" },
  { id: 5, name: "Fresh Carrot", price: 48.00, unit: "500 GM", image: "data:image/jpeg;base64," },
  { id: 6, name: "Cucumber", price: 36.00, unit: "500 GM", image: "data:image/jpeg;base64," },
  { id: 7, name: "Bottle Gourd (Lauki)", price: 44.00, unit: "1 KG", image: "data:image/jpeg;base64," },
  { id: 8, name: "Lady Finger (Okra)", price: 40.00, unit: "500 GM", image: "data:image/jpeg;base64," },
];

export default function RegularVegetablesPage() {
  // Independent state for quantities
  const [quantities, setQuantities] = useState(
    products.reduce((acc, product) => ({ ...acc, [product.id]: 1 }), {})
  );

  const updateQty = (id, delta) => {
    setQuantities(prev => ({
      ...prev,
      [id]: Math.max(1, prev[id] + delta)
    }));
  };

  return (
    <ShopLayout showHeader={true}>
      <div className="vegetables-page-container">
      {/*  Centered Heading Section */}
      <div className="page-header">
        <h1 className="main-heading">Regular Vegetables</h1>
        <p className="sub-heading">Fresh, juicy, and farm-direct regular vegetable varieties.</p>
      </div>

      {/*  Product Grid */}
      <div className="product-grid">
        {products.map((p) => {
          const currentQty = quantities[p.id];
          const totalAmount = (p.price * currentQty).toFixed(2); // Live calculation

          return (
            <div className="product-item" key={p.id}>
              <div className="img-holder">
                <img src={p.image} alt={p.name} />
              </div>
              
              <h4 className="p-title">{p.name}</h4>
              <div className="p-amount">Rs. {totalAmount}</div>
              <div className="p-unit-badge">{p.unit}</div>

              {/* Quantity Picker */}
              <div className="qty-picker">
                <button onClick={() => updateQty(p.id, -1)}>-</button>
                <input type="text" value={currentQty} readOnly />
                <button onClick={() => updateQty(p.id, 1)}>+</button>
              </div>
              
              {/* Green Add to Cart Button */}
              <button className="add-to-cart-btn">Add to Cart</button>
            </div>
          );
        })}
      </div>

      <style jsx global>{`
        body { margin: 0; font-family: 'Inter', sans-serif; background: #fff; }

        .vegetables-page-container { 
          max-width: 1400px; 
          margin: 0 auto; 
          padding: 60px 5%; 
        }

        /*  Centered Header Styling  */
        .page-header { 
          text-align: center; 
          margin-bottom: 50px; 
        }
        .main-heading { 
          font-size: 36px; 
          font-weight: 800; 
          color: #333; 
          margin: 0 0 10px 0; 
        }
        .sub-heading {
          font-size: 14px;
          color: #777;
          margin: 0;
        }

        .product-grid { 
          display: grid; 
          grid-template-columns: repeat(auto-fill, minmax(240px, 1fr)); 
          gap: 30px; 
        }

        .product-item { 
          border: 1px solid #f2f2f2; 
          border-radius: 12px; 
          padding: 20px; 
          text-align: center; 
          display: flex; 
          flex-direction: column; 
          transition: all 0.3s ease;
          background: #fff;
        }

        .product-item:hover { 
          transform: translateY(-5px); 
          box-shadow: 0 12px 30px rgba(0,0,0,0.07); 
        }

        .img-holder { 
          height: 180px; 
          display: flex; 
          align-items: center; 
          justify-content: center; 
          margin-bottom: 15px; 
        }
        .img-holder img { max-height: 100%; max-width: 100%; object-fit: contain; }

        .p-title { font-size: 15px; font-weight: 600; color: #444; height: 40px; margin: 5px 0; }
        .p-amount { font-size: 18px; font-weight: 700; color: #111; margin-bottom: 6px; }

        .p-unit-badge { 
          font-size: 11px; 
          background: #f8f8f8;
          color: #888;
          padding: 4px 12px;
          border-radius: 4px;
          display: inline-block;
          align-self: center;
          margin-bottom: 20px;
          font-weight: bold;
        }

        .qty-picker { 
          display: flex; 
          border: 1px solid #e0e0e0; 
          border-radius: 6px; 
          overflow: hidden; 
          align-self: center; 
          margin-bottom: 15px; 
        }
        .qty-picker button { background: #fff; border: none; padding: 8px 15px; cursor: pointer; font-size: 18px; }
        .qty-picker input { width: 40px; text-align: center; border-left: 1px solid #e0e0e0; border-right: 1px solid #e0e0e0; border-top: none; border-bottom: none; font-weight: 600; }

        .add-to-cart-btn { 
          background: #6aa333; 
          color: #fff; 
          border: none; 
          padding: 12px; 
          border-radius: 8px; 
          font-weight: 700; 
          font-size: 14px; 
          cursor: pointer; 
          margin-top: auto; 
          transition: background 0.2s;
        }
        .add-to-cart-btn:hover { background: #5a8d2a; }
      `}</style>
      </div>
    </ShopLayout>
  );
}